mapper.update:
update r_bcircle_member
set Book_Circle_ID = #{bookCircleId,jdbcType=INTEGER},
  MEMBER_ID = #{memberId,jdbcType=INTEGER}
where BC_MEM_ID = #{bcMemId,jdbcType=INTEGER}

mapper.select:
SELECT r_book_category.MCATG_ID as catgId,r_book_category.ISBN as isbn,book_inf.BOOK_NAME as bookName, book_inf.BRIEF_INTRO as briefIntro, book_inf.PIC_PATH as picPath FROM r_book_category,book_inf WHERE r_book_category.ISBN IN
#{id.isbn}
AND r_book_category.ISBN = book_inf.ISBN

SELECT
BC_MEM_ID, Book_Circle_ID, MEMBER_ID
from r_bcircle_member

     and BC_MEM_ID = #{bcMemId} 
     and Book_Circle_ID = #{bookCircleId} 
     and MEMBER_ID = #{memberId} 



        order by ${sort} ${order}


        order by BC_MEM_ID desc



    limit #{offset}, #{limit}
