mapper.select:
select * from  r_book_user_borrow

   and BOOK_ID = #{bookId} 
   and USER_ID = #{userId}

mapper.update:
update r_book_user_borrow

  `USR_BORROW_STATE`= #{usrBorrowState}, 


   and BOOK_ID = #{bookId} 
   and USER_ID = #{userId} 
   and BORROW_ID = #{borrowId}

mapper.select:
select   `BORROW_ID`, `BOOK_ID`, `USER_ID`, `USR_BORROW_STATE`, `BORROW_DATE_TIME`, `RETURN_DATE_TIME`,`BORROW_TIME`
from r_book_user_borrow

   and BORROW_ID = #{borrowId} 
   and BOOK_ID = #{bookId} 
   and USER_ID = #{userId} 
   and USR_BORROW_STATE = #{usrBorrowState} 
   and BORROW_DATE_TIME = #{borrowDateTime} 
   and RETURN_DATE_TIME = #{returnDateTime} 
   and BORROW_TIME = #{borrowTime} 



    order by ${sort} ${order}


    order by BORROW_ID desc



  limit #{offset}, #{limit}

select   `BORROW_ID`, `BOOK_ID`, `USER_ID`, `USR_BORROW_STATE`, `BORROW_DATE_TIME`, `RETURN_DATE_TIME`,`BORROW_TIME`
from r_book_user_borrow

   and BORROW_ID = #{borrowId} 
   and BOOK_ID = #{bookId} 
   and USER_ID = #{userId} 
   and BORROW_DATE_TIME = #{borrowDateTime} 
   and RETURN_DATE_TIME = #{returnDateTime} 
   and BORROW_TIME = #{borrowTime} 
  and USR_BORROW_STATE IN (1,2,3)
